mapper.select:
SELECT address_no AS addressNo, address, detail_address AS detailAddress, nickname, postal, main_address AS mainAddress
FROM address
WHERE user_id = #{userId}

mapper.update:
UPDATE address
SET main_address = 'Y'
WHERE user_id = #{userId}
AND address_no = #{addressNo}

UPDATE address
SET main_address = 'N'
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM address
WHERE user_id = #{userId}
AND address_no = #{addressNo}
