mapper.insert:
INSERT INTO container (
	container_location,
	contract_no
) VALUES (
	#{containerLocation},
	#{contractNo}
)

mapper.update:
UPDATE container
SET container_location = #{containerLocation},
    contract_no = #{contractNo}
WHERE container_no = #{containerNo}

mapper.insert:
INSERT INTO contract_order(
contract_no, order_time
) VALUES (
	#{contractNo}, NOW()
)
