mapper.delete:
DELETE FROM contract
WHERE contract_no = #{contractNo}
AND quotation_No = #{quotationNo}

mapper.update:
UPDATE contract
SET 
	 down_payment = #{downPayment}
	,final_payment = #{finalPayment}
	,down_payment_date = #{downPaymentDate}
	,final_payment_date = #{finalPaymentDate}
WHERE contract_no = #{contractNo}
