mapper.update:
UPDATE quotation
SET status = '승인'
WHERE quotation_no = #{quotationNo} AND sub_product_request_no = #{subProductRequestNo}

UPDATE quotation
SET status = '승인거절'
	,update_user = #{userId}
	,refusal_reason = #{rejectionReason}
	,update_date = NOW()
WHERE quotation_no = #{quotationNo} 
AND sub_product_request_no = #{subProductRequestNo}
